import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-stream-controls',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './stream-controls.component.html',
  styleUrls: ['./stream-controls.component.css']
})
export class StreamControlsComponent {
  @Input() isConnected = false;
  @Input() selectedClassification: '' | 'hostile' | 'friendly' = '';
  
  @Output() toggleStream = new EventEmitter<void>();
  @Output() clearTargets = new EventEmitter<void>();
  @Output() filterChange = new EventEmitter<'hostile' | 'friendly' | ''>();

  onToggleStream(): void {
    this.toggleStream.emit();
  }

  onClearTargets(): void {
    this.clearTargets.emit();
  }

  onFilterChange(): void {
    this.filterChange.emit(this.selectedClassification);
  }
}
