import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, interval } from 'rxjs';
import { map, switchMap } from 'rxjs/operators';
import { Target } from '../models/target.model';

@Injectable({
  providedIn: 'root'
})
export class TargetService {
  private targetsSubject = new BehaviorSubject<Target[]>([]);
  private isConnectedSubject = new BehaviorSubject<boolean>(false);
  
  public targets$ = this.targetsSubject.asObservable();
  public isConnected$ = this.isConnectedSubject.asObservable();

  private apiUrl = 'http://localhost:8000';
  private wsUrl = 'ws://localhost:8000/ws';

  constructor(private http: HttpClient) {}

  // Simulate WebSocket stream using RxJS interval
  startSimulatedStream(): void {
    this.isConnectedSubject.next(true);
    
    interval(1000).pipe(
      map(() => this.generateRandomTarget())
    ).subscribe(target => {
      const currentTargets = this.targetsSubject.value;
      const updatedTargets = [target, ...currentTargets].slice(0, 50); // Keep last 50 targets
      this.targetsSubject.next(updatedTargets);
    });
  }

  stopSimulatedStream(): void {
    this.isConnectedSubject.next(false);
  }

  // REST API methods
  getTargets(): Observable<{targets: Target[], count: number}> {
    return this.http.get<{targets: Target[], count: number}>(`${this.apiUrl}/targets`);
  }

  addTargets(targets: Target[]): Observable<any> {
    return this.http.post(`${this.apiUrl}/targets`, { targets });
  }

  // Filter targets by classification
  getFilteredTargets(classification?: 'hostile' | 'friendly'): Observable<Target[]> {
    return this.targets$.pipe(
      map(targets => {
        if (!classification) return targets;
        return targets.filter(target => target.classification === classification);
      })
    );
  }

  // Generate random target for simulation
  private generateRandomTarget(): Target {
    const classifications: ('hostile' | 'friendly')[] = ['hostile', 'friendly'];
    const names = ['Alpha', 'Bravo', 'Charlie', 'Delta', 'Echo', 'Foxtrot', 'Golf', 'Hotel'];
    
    return {
      id: Math.random().toString(36).substr(2, 9),
      name: `${names[Math.floor(Math.random() * names.length)]}-${Math.floor(Math.random() * 1000)}`,
      heading: Math.floor(Math.random() * 360),
      timestamp: new Date().toISOString(),
      classification: classifications[Math.floor(Math.random() * classifications.length)]
    };
  }

  // Clear all targets
  clearTargets(): void {
    this.targetsSubject.next([]);
  }
}
